document.addEventListener("DOMContentLoaded",(()=>{let e=document.querySelector(".header");window.addEventListener("scroll",(function(){window.scrollY>20?e.classList.add("sticky"):e.classList.remove("sticky")}));const o=e=>{e.querySelector(".dropdown-menu").classList.toggle("show")};document.querySelectorAll(".dropdown-toggle").forEach((e=>{e.addEventListener("click",(()=>{const t=e.closest(".dropdown");o(t)}))})),document.addEventListener("click",(e=>{document.querySelectorAll(".dropdown").forEach((o=>{if(!o.contains(e.target)){const e=o.querySelector(".dropdown-menu");e.classList.contains("show")&&e.classList.remove("show")}}))}));document.querySelectorAll(".dropdown-menu li a").forEach((e=>{e.addEventListener("click",(()=>{const t=e.closest(".dropdown");o(t)}))}))}));
